var Actions={init:function(){selectAll=document.getElementById("action-toggle");if(selectAll){selectAll.style.display="inline";addEvent(selectAll,"change",function(){Actions.checker(this.checked)})}},checker:function(b){actionCheckboxes=document.getElementsBySelector("tr input.action-select");for(var a=0;a<actionCheckboxes.length;a++){actionCheckboxes[a].checked=b}}};addEvent(window,"load",Actions.init);